import React from "react";
import { Resgister } from "./register.jsx";
export class Login extends React.Component {
  constructor(props) {
    super(props);
    this.routeChange = this.routeChange.bind(this);
  }
 
  routeChange() {
    let path = "./SignUp";
    this.props.history.push(path);
  }
  state = {
    isActive: true,
  };
 
  handleShow = () => {
    this.setState({
      isActive: true,
    });
  };
 
  handleHide = () => {
    this.setState({
      isActive: false,
    });
  };
  render() {
    return (
      <div className="base-container auth-wrapper" ref={this.props.containerRef}>
        {this.state.isActive ? (<form>
                    <h3 className="title">Sign In</h3>
                    <div className="form-group">
                    <img className="img" src={require('./loginImg.JPG')} alt="nothing"/>
                    </div>
                    <div className="form-group">
                        <label className="form-label"><i class='fas fa-envelope' ></i>{" "}Email address</label>
                        <input type="email" className="form-control" placeholder="Enter email" />
                        <label className="form-label"> <i class='fas fa-key'  ></i>{" "}Password</label>
                        <input type="password" className="form-control" placeholder="Enter password" />
                        
                    </div>
                    <div className="custom-control custom-checkbox">
                    <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                        </div>
                        <div className="form-group forgot-password text-right">
                        <button type="submit" onClick={this.routeChange} className="btn btn-primary btn-block">Login</button>
                        <button className=" btn btn-link " onClick={this.handleHide}>Forgot password?</button>
                    </div>
                </form>
         
        ) : (
            <form>
                <h3 className="title">Sign In</h3>
            <div className="form-group">
              <img className="img" src={require('./forgot_password.JPG')} alt="nothing"/>
            </div>
            <div className="form-group">
            <label className="form-label">Email address</label>
            <input type="email" className="form-control" placeholder="Enter email" />
          </div>
          <div className="form-group">
            <button type="submit" className="btn btn-primary btn-block">Send reset link</button>
          </div>

      </form>
        )}
      </div>
    );
  }
}
